void acCharData(int f_INDEX, int f_CDATID, int f_BDATID, int f_TEAM, int f_NetAvatar)
	{
	player[g_Player]->BFO.AVATARDATA[f_INDEX].LIVE = 1;

	if(f_BDATID > 8) f_BDATID = 4;

	player[g_Player]->BFO.AVATARDATA[f_INDEX].CDATID = f_CDATID;
	player[g_Player]->BFO.AVATARDATA[f_INDEX].BDATID = f_BDATID;

	player[g_Player]->BFO.AVATARDATA[f_INDEX].TEAM = f_TEAM;

	player[g_Player]->BFO.AVATARDATA[f_INDEX].NETAVATAR = f_NetAvatar;

	if(f_NetAvatar == 1)
		{
		//### Determine if avatar is already indexed ###//
		bool f_Present = false;

		for(int f_AVID = 0; f_AVID < player[g_Player]->BFO.NomNetAvatars; f_AVID++)
			{
			if(player[g_Player]->BFO.NetAvatarID[f_AVID] == f_INDEX) f_Present = true;
			}

		if(!f_Present)
			{
			player[g_Player]->BFO.NetAvatarID[player[g_Player]->BFO.NomNetAvatars] = f_INDEX;
			player[g_Player]->BFO.NomNetAvatars++;
			}
		}
	}

int scanread( char* f_Input, int f_VarSize, int f_CharCount, FILE* f_Stream)
{
	for(int i = 0; i < f_CharCount; i++)
		{
		fscanf(f_Stream, "%c", &f_Input[i]);
		if(f_Input[i] == ' ') return 0;
		if(f_Input[i] == '\n') return 0;
		}
	return 1;
}

int scanreadx( char* f_Input, int f_VarSize, int f_CharCount, FILE* f_Stream)
{
	char f_Buffer[255];
	int f_ScanCount = 0;
	int f_RecieveCount = 0;
	int f_State = 0;
	int f_SpaceCount = 0;
	int f_SpacePointer;
	while(f_RecieveCount < f_CharCount)
		{
		fscanf(f_Stream, "%c", &f_Buffer[f_ScanCount]);

		switch(f_State)
			{
			case 0:
				{
				if(f_Buffer[f_ScanCount] != ' ' && f_Buffer[f_ScanCount] != '\n')
					{
					f_Input[f_RecieveCount] = f_Buffer[f_ScanCount];
					f_RecieveCount++;
					f_State = 1;
					}
				}break;
				
			case 1:
				{
				if(f_Buffer[f_ScanCount] == ' ')
					{
					f_SpaceCount++;
					}	
				else
					{
					if(f_Buffer[f_ScanCount] == '\n') return 0;
					else
						{
						f_SpacePointer = f_RecieveCount;
						while(f_SpaceCount > 0)
							{
							f_Input[f_SpacePointer] = ' ';
							f_SpaceCount--;
							f_SpacePointer++;
							f_RecieveCount++;
							}
						f_Input[f_RecieveCount] = f_Buffer[f_ScanCount];
						}
					}
				
				f_RecieveCount++;
				}break;
			}

		f_ScanCount++;
		}
	return 1;
}

