		{
		printf("ERROR Character Data File is corrupt / unreadable / not present");
		for(;;) {}
		}

	//##### - ENCRYPTION SYSTEM - FIND CODE
	EncryptionCode = 100;

	fscanf(stream,"%d",&Integer);

	EncryptionCode += Integer;

	fscanf(stream,"%d",&Integer);

	EncryptionCode -= Integer;

	fscanf(stream,"%d",&Integer);

	EncryptionCode += Integer;

	fscanf(stream,"%d",&Integer);

	EncryptionCode -= Integer;


